import React from 'react';
import ReactDom from 'react-dom';
import './index.css'
import { Header, List, Details, NotFound, PopUp, AccountDetails } from './components';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import MyProfile from './components/accountDetails/MyProfile';
import MyPurchases from './components/accountDetails/MyPurchases';


class App extends React.Component{
  constructor() {
    super();

    this.currentUser = {

    };

    this.state = {
      openPopUp: false,
      createAccount: false,
      signedIn: this.currentUser.signedIn,
      accountDetails: false,
      error: false,
      purchases: [],
      users: JSON.parse(localStorage.getItem('users')) || [],
    }
  }

  createAccountCheck = async(users, user) => {
    this.setState({error: false}, () => {
      users.forEach(us => {
      if(us.email == user.email) {
        this.setState({error : true})
      }
    })
    })

  }

  createAccountSubmit = async(user) => {
    const newUsers = JSON.parse(localStorage.getItem('users')) ? [...JSON.parse(localStorage.getItem('users'))] : [];
    user.id = newUsers.length+1;
    user.purchases = [];
    await this.createAccountCheck(newUsers, user);

    if(this.state.error) {
      return
    }
    newUsers.push(user)
    localStorage.setItem('users', JSON.stringify(newUsers))

      this.setState({openPopUp: false, signedIn: true})
      this.currentUser = user;
    }


  openPopUp = (type) => {
    this.setState({error: false})
    if(type == 'create') {
      this.setState({openPopUp: true, createAccount: true})

      return;
    }

    this.setState({openPopUp: true})
  }

  closePopUp = (e,ref) => {
    if(ref && ref.current.contains(e.target)) {
      return;
    }
    this.setState({openPopUp: false, createAccount: false})
  }

  submitSignInCheck = (users, user) => {
    this.setState({error: true}, () => {
      if(!users) {
      return;
    }
      users.forEach(us => {
        if(us.email == user.email && us.password == user.password) {
          this.currentUser = us;
          this.setState({error: false});

          return
        }
    })
    })

  }

  submitSignIn = async(user) => {
    const newUsers = JSON.parse(localStorage.getItem('users'));

    await this.submitSignInCheck(newUsers,user);

    if(this.state.error) {
      return;
    }

    this.setState({openPopUp: false, signedIn: true})
  }

  openAccountDetails = () => {
    this.setState({accountDetails: true})
  }

  closeDetails = (e,ref) => {
    if(ref && ref.current.contains(e.target)) {
      return;
    }

    this.setState({accountDetails: false})
  }

  logout = () => {
    this.setState({signedIn: false, accountDetails: false, purchases: []})
  }

setPurchases = (id, buy, name, count, price) => {
    const users = JSON.parse(localStorage.getItem('users'));

    const purchase = {
      id,
      type: buy ? 'buy' : 'sell',
      name,
      count,
      price: price*count,
    }



    const newUsers = users.map(user => {
      if(user.id == this.currentUser.id) {
        user.purchase = user.purchases ? user.purchases.push(purchase) : [purchase];
        this.currentUser = user;
      }
      return user;
    });

    this.setState({users: newUsers}, () => {
      localStorage.setItem('users', JSON.stringify(newUsers));
    });


  }

  render() {
    const {openPopUp, signedIn , createAccount, error, accountDetails, users, purchases} = this.state;

      return (
      <BrowserRouter>
        <div>
          <Header openPopUp={this.openPopUp} signedIn={signedIn} openAccountDetails={this.openAccountDetails}/>
          {openPopUp ? <PopUp closePopUp={this.closePopUp} createAccountSubmit={this.createAccountSubmit} createAccount={createAccount} submitSignIn={this.submitSignIn} error={error}/> : null}
          {accountDetails ? <AccountDetails closeDetails={this.closeDetails} logout={this.logout} id={this.currentUser.id}/> : null}
          <Switch>
            <Route exact path={'/'} component={List}/>
            <Route path={'/currency/:id'} component={(props => <Details {...props} signedIn={signedIn} setPurchases={this.setPurchases}/>)}/>
            <Route path={'/myprofile/:id'} render={(props) => <MyProfile {...props} users={users} />}/>
            <Route path={'/mypurchases/:id'} render={(props) => <MyPurchases {...props} users={users} currentUser={this.currentUser}/>}/>
            <Route component={NotFound}/>
          </Switch>
        </div>
      </BrowserRouter>
    )
  }
  }



ReactDom.render(
   <App/>,
  document.getElementById('root')
)
