import React, { Component } from 'react'
import './AccountDetails.css';
import { Link } from 'react-router-dom';

export default class AccountDetails extends Component {

    constructor() {
        super();

        this.ref = React.createRef();
    }

    closeDetailsWrapper = (e) => {
        const {closeDetails} = this.props;

        closeDetails(e,this.ref)
    }

    componentDidMount() {
        setTimeout(() => {
            document.addEventListener('click', this.closeDetailsWrapper);
        },200)
    }

    componentWillUnmount() {
        document.removeEventListener('click', this.closeDetailsWrapper);
    }


  render() {
      const {logout,id} = this.props;

    return (
      <div ref={this.ref} className='Account-Details'>
          <Link to={`/myprofile/${id}`} className='link'>
            <span>My Profile</span>
        </Link>
        <Link to={`/mypurchases/${id}`} className='link'>
          <span>My Purchases</span>
        </Link>

        <Link to={'/'} className='link'>
            <span onClick={logout}>Logout</span>
        </Link>
      </div>
    )
  }
}
