import React, { useState } from 'react';
import ReactDom from 'react-dom';
import './index.css'
import { Header, List, Details, NotFound, PopUp, AccountDetails } from './components';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import MyProfile from './components/accountDetails/MyProfile';
import MyPurchases from './components/accountDetails/MyPurchases';


const App = () => {

  const currentUser = {};
  const [openPopUp, setOpenPopUp] = useState(false);
  const [createAccount, setCreateAccount] = useState(false);
  const [signedIn, setSignedIn] = useState(currentUser.signedIn);
  const [accountDetails, setAccountDetails] = useState(false);
  const [error, setError] = useState(false);
  const [users, setUsers] = useState(JSON.parse(localStorage.getItem('users')) || []);

  const createAccountCheck = async(users, user) => {
    setError(false);



    // this.setState({error: false}, () => {
    //   users.forEach(us => {
    //   if(us.email == user.email) {
    //     this.setState({error : true})
    //   }
    // })
    // })

  }

  createAccountSubmit = async(user) => {
    const newUsers = JSON.parse(localStorage.getItem('users')) ? [...JSON.parse(localStorage.getItem('users'))] : [];
    user.id = newUsers.length+1;
    user.purchases = [];
    await this.createAccountCheck(newUsers, user);

    if(this.state.error) {
      return
    }
    newUsers.push(user)
    localStorage.setItem('users', JSON.stringify(newUsers))

      this.setState({openPopUp: false, signedIn: true})
      this.currentUser = user;
    }


  openPopUp = (type) => {
    this.setState({error: false})
    if(type == 'create') {
      this.setState({openPopUp: true, createAccount: true})

      return;
    }

    this.setState({openPopUp: true})
  }

  closePopUp = (e,ref) => {
    e.stopPropagation();

    if(ref.current.contains(e.target)) {
      return;
    }
    this.setState({openPopUp: false, createAccount: false})
  }

  submitSignInCheck = (users, user) => {
    this.setState({error: true}, () => {
      if(!users) {
      return;
    }
      users.forEach(us => {
        if(us.email == user.email && us.password == user.password) {
          this.currentUser = us;
          this.setState({error: false});

          return
        }
    })
    })

  }

  submitSignIn = async(user) => {
    const newUsers = JSON.parse(localStorage.getItem('users'));

    await this.submitSignInCheck(newUsers,user);

    if(this.state.error) {
      return;
    }

    this.setState({openPopUp: false, signedIn: true})
  }

  openAccountDetails = (e) => {
    e.stopPropagation();

    this.setState({accountDetails: true})
  }

  closeDetails = (e,ref) => {
    if(ref && ref.current.contains(e.target)) {
      return;
    }

    this.setState({accountDetails: false})
  }

  logout = () => {
    this.setState({signedIn: false, accountDetails: false, purchases: []})
  }

setPurchases = (id, buy, name, count, price) => {
    const users = JSON.parse(localStorage.getItem('users'));

    const purchase = {
      id,
      type: buy ? 'buy' : 'sell',
      name,
      count,
      price: price*count,
    }



    const newUsers = users.map(user => {
      if(user.id == this.currentUser.id) {
        user.purchase = user.purchases ? user.purchases.push(purchase) : [purchase];
        this.currentUser = user;
      }
      return user;
    });

    this.setState({users: newUsers}, () => {
      localStorage.setItem('users', JSON.stringify(newUsers));
    });


  }


      return (
      <BrowserRouter>
        <div>
          <Header openPopUp={openPopUp} signedIn={signedIn} openAccountDetails={openAccountDetails}/>
          {openPopUp ? <PopUp closePopUp={closePopUp} createAccountSubmit={createAccountSubmit} createAccount={createAccount} submitSignIn={submitSignIn} error={error}/> : null}
          {accountDetails ? <AccountDetails closeDetails={closeDetails} logout={logout} id={currentUser.id}/> : null}
          <Routes>
            <Route path={'/'} element={<List />}/>
            <Route path={'/currency/:id'} element={(<Details signedIn={signedIn} setPurchases={setPurchases}/>)}/>
            <Route path={'/myprofile/:id'} element={<MyProfile users={users} />}/>
            <Route path={'/mypurchases/:id'} element={<MyPurchases users={users} currentUser={currentUser}/>}/>
            <Route path='*' element={<NotFound />} />
          </Routes>
        </div>
      </BrowserRouter>
    )
  }


ReactDom.render(
   <App/>,
  document.getElementById('root')
)
